function getFolderNames() {
  let tables = game.folders.filter(f => f.type === 'Item').reduce((acc, table) => {
    acc.push({ key: table.id, name: table.name });
    return acc;
  }, []);
  return tables;
}

let folders = getFolderNames();
let content = `<form><div style="display: inline-block; width: 100%; margin-bottom: 10px">
  <p>This script will transform items in a folder to another type. </p>
  <label for="output-targetFolder" style="vertical-align: top; margin-right: 10px;">Select Folder:</label>
  <br /><select name='output-targetFolder' id='output-targetFolder'>`

folders.forEach(item => {
  content += `<option value='${item.key}'>${item.name}</option>`;
});

content += `</select><br/><label for="output-sourceType" style="vertical-align: top; margin-right: 10px;">Source Type:</label><br /><select name="output-sourceType" id="output-sourceType">`;

['talent', 'trait', 'quality'].forEach(x => {
  content += `<option value='${x}'>${x}</option>`;
});

content += `</select><br/><label for="output-targetType" style="vertical-align: top; margin-right: 10px;">Target Type:</label><br /><select name="output-targetType" id="output-targetType">`;

['trait', 'quality', 'talent'].forEach(x => {
  content += `<option value='${x}'>${x}</option>`;
});

content += `</select></div><br /></form>`;

const convertTypes = async (f, st, tt) => {
  const x = game.items.filter(i => i.folder?.id === f && i.type === st);

  for (let i of x) {
    await i.update({ type: tt });
  };
}

new Dialog({
  title: `GeekDad's Compendium to Rolltable Converter`,
  content: content,
  buttons: {
    yes: {
      icon: "<i class='fas fa-check'></i>",
      label: "Convert",
      callback: (html) => {
        let targetFolder = html.find("select[name='output-targetFolder']").val();
        let sourceType = html.find("select[name='output-sourceType']").val();
        let targetType = html.find("select[name='output-targetType']").val();
        convertTypes(targetFolder, sourceType, targetType);
      }
    },
    no: {
      icon: "<i class='fas fa-times'></i>",
      label: 'Cancel'
    }
  },
  default: "yes"
}).render(true);
