const pack = game.packs.get('zweihander.zh-trappings');
const docs = await pack.getDocuments();
const catFolders = {};
for (let doc of docs) {
  const cat = doc.system.details.category['@en'] || 'Trappings';
  if (!(cat in catFolders)) {
    catFolders[cat] = await Folder.create({type: "Item", name: cat});
  }
  const folder = catFolders[cat];
  const d = doc.toObject();
  d.folder = folder.id;
  await Item.create(d);
}
