const defaultIcons = [
    ...Object.values(CONFIG.ZWEI.defaultActorIcons),
    ...Object.values(CONFIG.ZWEI.defaultItemIcons),
];

const oldDefaultIcons = defaultIcons.map(i => i.replace('/assets/default-icons/', '/assets/icons/'));

for (const doc of [...game.actors.contents, ...game.items.contents]) {
    if (oldDefaultIcons.includes(doc.img)) {
        console.log(doc.img);
        await doc.update({
            img: defaultIcons[oldDefaultIcons.indexOf(doc.img)]
        })
    }
}
